    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //sentadillas
        Ejercicio ejercicio = null;
        for (Ejercicio e : Ejercicio.getEjerciciosDisponibles()) {
            if (e.getNombre().equals("Sentadillas")) {
                ejercicio = e;
                break;
            }
        }
        if (ejercicio != null) {
            if (ventanaCrearRutina == null) {
                ventanaCrearRutina = new crearRutina();
            }
            ventanaCrearRutina.agregarEjercicio(ejercicio);
            
            ventanaCrearRutina.setLocationRelativeTo(null);
            
        }
    }//GEN-LAST:event_jButton2ActionPerformed

