    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (ventanaCrearRutina == null) {
        ventanaCrearRutina = new crearRutina();
    }

        // Verificar si hay al menos 5 ejercicios
        if (ventanaCrearRutina.getEjerciciosAgregados().size() < 5) {
            JOptionPane.showMessageDialog(this, "Debe agregar al menos 5 ejercicios antes de continuar.");
            return; // No avanza
        }

        ventanaCrearRutina.setVisible(true);
        ventanaCrearRutina.setLocationRelativeTo(null);
        this.dispose(); // Cierra esta ventana (lista)
    }//GEN-LAST:event_jButton1ActionPerformed

